    color: COLORS.black,
    marginBottom: 8
  },
  scanConfidence: {
    fontSize: 14,
    color: COLORS.secondary,
    fontWeight: '600'
  },
  extractedDataCard: {
    backgroundColor: COLORS.white,
    borderRadius: 12,
    padding: 16,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: COLORS.black,
    marginBottom: 8
  },
  editHint: {
    fontSize: 12,
    color: COLORS.gray[500],
    fontStyle: 'italic',
    marginBottom: 12
  },
  actionButtons: {
